mapper.select:
select

from tbl_access_record
where 1=1

	and id = #{entity.id,jdbcType=BIGINT}


	and household_id = #{entity.houseHold.id,jdbcType=BIGINT}


	and household_name = #{entity.houseHold.householdName,jdbcType=VARCHAR}


	and out_off_time = #{entity.outOffTime,jdbcType=TIMESTAMP}


	and out_off = #{entity.outOff,jdbcType=CHAR}


	and photo_url = #{entity.photoUrl,jdbcType=VARCHAR}


	and community_id = #{entity.community.id,jdbcType=BIGINT}


	and community_name = #{entity.community.name,jdbcType=VARCHAR}


	and camera_id = #{entity.camara.id,jdbcType=BIGINT} 


	and camera_name = #{entity.camara.name,jdbcType=VARCHAR} 


	and (household_name LIKE '%${searchContent}%' or community_name LIKE '%${searchContent}%' or camera_name LIKE '%${searchContent}%') 

ORDER BY tbl_access_record.id desc LIMIT #{startRow}, #{pageSize}

select
count(*)
from tbl_access_record
where 1=1

	and id = #{entity.id,jdbcType=BIGINT}


	and household_id = #{entity.houseHold.id,jdbcType=BIGINT}


	and household_name = #{entity.houseHold.householdName,jdbcType=VARCHAR}


	and out_off_time = #{entity.outOffTime,jdbcType=TIMESTAMP}


	and out_off = #{entity.outOff,jdbcType=CHAR}


	and photo_url = #{entity.photoUrl,jdbcType=VARCHAR}


	and community_id = #{entity.community.id,jdbcType=BIGINT}


	and community_name = #{entity.community.name,jdbcType=VARCHAR}


	and camera_id = #{entity.camara.id,jdbcType=BIGINT} 


	and camera_name = #{entity.camara.name,jdbcType=VARCHAR} 


	and (household_name LIKE '%${searchContent}%' or community_name LIKE '%${searchContent}%' or camera_name LIKE '%${searchContent}%')
