mapper.select:
select

from tbl_access_record, tbl_household, tbl_camera, tbl_control, tbl_door
where 1=1

	and tbl_access_record.id = #{entity.id,jdbcType=BIGINT}


	and tbl_access_record.household_id = #{entity.houseHold.id,jdbcType=BIGINT}


	and tbl_access_record.household_name = #{entity.houseHold.householdName,jdbcType=VARCHAR}


	and tbl_access_record.out_off_time = #{entity.outOffTime,jdbcType=TIMESTAMP}


	and tbl_access_record.out_off = #{entity.outOff,jdbcType=CHAR}


	and tbl_access_record.photo_url = #{entity.photoUrl,jdbcType=VARCHAR}


	and tbl_access_record.community_id = #{entity.community.id,jdbcType=BIGINT}


	and tbl_access_record.community_name = #{entity.community.name,jdbcType=VARCHAR}


	and tbl_access_record.camera_id = #{entity.camara.id,jdbcType=BIGINT} 


	and tbl_access_record.camera_name = #{entity.camara.name,jdbcType=VARCHAR} 

	and tbl_access_record.household_id = tbl_household.id 
	and tbl_access_record.camera_id = tbl_camera.id 
	and tbl_camera.control_id = tbl_control.id  
	and tbl_control.door_id = tbl_door.id 

	and (tbl_access_record.household_name LIKE '%${searchContent}%' or tbl_access_record.community_name LIKE '%${searchContent}%' or tbl_door.name LIKE '%${searchContent}%') 

ORDER BY tbl_access_record.id desc LIMIT #{startRow}, #{pageSize}

select
count(*)
from tbl_access_record
where 1=1

	and id = #{entity.id,jdbcType=BIGINT}


	and household_id = #{entity.houseHold.id,jdbcType=BIGINT}


	and household_name = #{entity.houseHold.householdName,jdbcType=VARCHAR}


	and out_off_time = #{entity.outOffTime,jdbcType=TIMESTAMP}


	and out_off = #{entity.outOff,jdbcType=CHAR}


	and photo_url = #{entity.photoUrl,jdbcType=VARCHAR}


	and community_id = #{entity.community.id,jdbcType=BIGINT}


	and community_name = #{entity.community.name,jdbcType=VARCHAR}


	and camera_id = #{entity.camara.id,jdbcType=BIGINT} 


	and camera_name = #{entity.camara.name,jdbcType=VARCHAR} 


	and (household_name LIKE '%${searchContent}%' or community_name LIKE '%${searchContent}%' or camera_name LIKE '%${searchContent}%')
