mapper.insert:
insert into tbl_camera (name, control_id, direction, ip, relay_id
)
values (#{name,jdbcType=VARCHAR}, #{control.id,jdbcType=BIGINT},
#{direction,jdbcType=VARCHAR}, #{ip,jdbcType=VARCHAR}, #{relay_id,jdbcType=VARCHAR}
)

mapper.select:
select

from tbl_camera
where 1=1

	and id = #{id,jdbcType=BIGINT}


	and name = #{name,jdbcType=VARCHAR}


	and control_id = #{control.id,jdbcType=BIGINT}


	and direction = #{direction,jdbcType=VARCHAR}
