mapper.insert:
insert into tbl_camera (name, control_id, direction, ip, relay_id
)
values (#{name,jdbcType=VARCHAR}, #{control.id,jdbcType=BIGINT},
#{direction,jdbcType=VARCHAR}, #{ip,jdbcType=VARCHAR}, #{relay_id,jdbcType=VARCHAR}
)

mapper.select:
select
a.id as camera_id, a.name, a.direction, a.ip as camera_ip, a.relay_id, a.control_id, b.name as control_name, b.ip, b.port 
from tbl_camera a, tbl_control b 
where 1=1

	and a.id = #{id,jdbcType=BIGINT}


	and a.name = #{name,jdbcType=VARCHAR}


	and a.control_id = #{control.id,jdbcType=BIGINT}


	and a.direction = #{direction,jdbcType=VARCHAR}

	and a.control_id = b.id
