mapper.select:
select

from tbl_household
where 1=1

	and id = #{id,jdbcType=BIGINT}


	and household_name = #{householdName,jdbcType=VARCHAR}


	and gender = #{gender,jdbcType=CHAR}


	and tel = #{tel,jdbcType=VARCHAR}


	and identify_card = #{identifyCard,jdbcType=VARCHAR}


	and room_path = #{roomPath,jdbcType=VARCHAR}


	and photo_id = #{photoId,jdbcType=VARCHAR}


	and card_number = #{cardNumber,jdbcType=VARCHAR}


	and remarks = #{remarks,jdbcType=VARCHAR}

select
from tbl_household where 1=1
and community_id = #{communityId,jdbcType=BIGINT}
and ( household_name like '%${searchContent}%' or tel like '%${searchContent}%' or gender like '%${searchContent}%' or room_path like '%${searchContent}%' or card_number like '%${searchContent}%' or household_type like '%${searchContent}%' or identify_card like '%${searchContent}%')
LIMIT #{startRow}, #{pageSize}

select count(*) from tbl_household where 1=1
and community_id = #{communityId ,jdbcType=BIGINT}
and (household_name like '%${searchContent}%' or tel like '%${searchContent}%' or gender like '%${searchContent}%' or room_path like '%${searchContent}%' or card_number like '%${searchContent}%' or household_type like '%${searchContent}%' or identify_card like '%${searchContent}%')
